\n        ::-webkit-scrollbar-thumb:hover {\r\n            background: var(--text-secondary);\r\n        }\r\n    </style>\r\n\r\n    <script>\r\n        const STATIC_UPLOAD_BASE = "{{ url_for('static', filename='uploads') }}";\r\n        const buildImageUrl = (conversationId, filename) => {\r\n            return `${STATIC_UPLOAD_BASE}/ai_attachments/${conversationId}/${filename}`;\r\n        };\r\n\r\n        const renderer = new marked.Renderer();\r\n        renderer.table = (header, body) => `\r\n            <div class="overflow-x-auto my-4 border border-gray-200 rounded-lg">\r\n                <table class="min-w-full table-auto text-sm">\r\n                    <thead class="bg-blue-500 text-white">${header}</thead>\r\n                    <tbody>${body}</tbody>\r\n                </table>\r\n            </div>\r\n        `;\r\n\r\n        marked.setOptions({\r\n            renderer,\r\n            highlight: function (code, lang) {\r\n                if (lang && hljs.getLanguage(lang)) {\r\n                    return hljs.highlight(code, { language: lang }).value;\r\n                }\r\n                return hljs.highlightAuto(code).value;\r\n            },\r\n            breaks: true,\r\n            gfm: true,\r\n            sanitize: false,\r\n            pedantic: false,\r\n            smartLists: true,\r\n            smartypants: true\r\n        });\r\n    </script>\r\n</head>\r\n\r\n<body class="h-screen overflow-hidden">\r\n    <div class="h-screen flex flex-col">\r\n        <!-- Header - Always visible -->\r\n        <header class="flex-shrink-0 bg-white dark:bg-slate-800 border-b border-gray-200 dark:border-slate-700">\r\n            <div class="px-3 sm:px-4 h-14 flex justify-between items-center">\r\n                <div class="flex items-center space-x-2">\r\n                    <button id="toggleConversationsBtn"\r\n                        class="text-gray-700 dark:text-gray-300 hover:text-blue-600 dark:hover:text-blue-400 transition-colors p-2 roun