ax-w-md w-full max-h-[90vh] overflow-hidden animate-slide-in">\r\n            <!-- Modal Header -->\r\n            <div\r\n                class="bg-gradient-to-r from-blue-500 to-blue-600 dark:from-blue-600 dark:to-blue-700 px-6 py-4 flex justify-between items-center">\r\n                <h3 class="text-xl font-bold text-white flex items-center">\r\n                    <i class="fas fa-cog mr-3"></i>\r\n                    Paramètres\r\n                </h3>\r\n                <button id="closeSettingsBtn" class="text-white/80 hover:text-white transition-colors">\r\n                    <i class="fas fa-times text-xl"></i>\r\n                </button>\r\n            </div>\r\n\r\n            <!-- Modal Body -->\r\n            <div class="p-6 overflow-y-auto max-h-[calc(90vh-180px)]">\r\n                <div class="space-y-6">\r\n                    <!-- Tone Setting -->\r\n                    <div class="space-y-2">\r\n                        <label class="block text-sm font-semibold text-gray-700 dark:text-gray-300 flex items-center">\r\n                            <i class="fas fa-comments mr-2 text-blue-500"></i>\r\n                            Ton de l'assistant\r\n                        </label>\r\n                        <select id="toneSetting"\r\n                            class="w-full px-4 py-2 rounded-lg border border-gray-300 dark:border-slate-600 bg-white dark:bg-slate-700 text-gray-900 dark:text-gray-100 focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all">\r\n                            <option value="professional">Professionnel</option>\r\n                            <option value="friendly">Amical</option>\r\n                            <option value="casual">Décontracté</option>\r\n                            <option value="formal">Formel</option>\r\n                            <option value="creative">Créatif</option>\r\n                        </select>\r\n                        <p class="text-xs text-gray-500 dark:text-gray-400 mt-1">\r\n                            <i class="fas fa-info-circle mr-1"></i>\r\n                            Définit le style de communication de l'IA\r\n                        </p>\r\n                    </div>\r\n\r\n                    <!-- Theme Toggle -->\r\n                    <div class="space-y-2">\r\n                        <label class="block text-sm font-semibold text-gray-700 dark:text-gray-300 flex items-center">\r\n                            <i class="fas fa-palette mr-2 text-purple-500"></i>\r\n                            Thème\r\n                        </label>\r\n                        <div\r\n                            class="flex items-center justify-between p-4 bg-gray-50 dark:bg-slate-700 rounded-lg border border-gray-200 dark:border-slate-600">\r\n                            <div class="flex items-center space-x-3">\r\n                                <i class="fas fa-sun text-yellow-500 text-lg"></i>\r\n                                <span class="text-sm font-medium text-gray-700 dark:text-gray-300">Mode sombre</span>\r\n                                <i class="fas fa-moon text-blue-400 text-lg"></i>\r\n                            </div>\r\n                            <label class="toggle-switch">\r\n                                <input type="checkbox" id="themeToggle">\r\n                                <span class="toggle-slider"></span>\r\n                            </label>\r\n                        </div>\r\n                    </div>\r\n\r\n                    <!-- Language Setting -->\r\n                    <div class="space-y-2">\r\n                        <label class="block text-sm font-semibold text-gray-700 dark:text-gray-300 flex items-center">\r\n                            <i class="fas fa-language mr-2 text-green-500"></i>\r\n                            Langue de réponse\r\n                        </label>\r\n                        <select id="languageSetting"\r\n                            class="w-full px-4 py-2 rounded-lg border border-gray-300 dark:border-slate-600 bg-white dark:bg-slate-700 text-gray-900 dark:text-gray-100 focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all">\r\n                            <option value="fr">Français</option>\r\n                            <option value="en">English</option>\r\n                            <option value="es">Español</option>\r\n                            <option value="de">Deutsch</option>\r\n                        </select>\r\n                    </div>\r\n\r\n                    <!-- Response Length -->\r\n                    <div class="space-y-2">\r\n                        <label class="block text-sm font-semibold text-gray-700 dark:text-gray-300 flex items-center">\r\n                            <i class="fas fa-text-height mr-2 text-orange-500"></i>\r\n                            Longueur des réponses\r\n                        </label>\r\n                        <select id="responseLengthSetting"\r\n                            class="w-full px-4 py-2 rounded-lg border border-gray-300 dark:border-slate-600 bg-white dark:bg-slate-700 text-gray-900 dark:text-gray-100 focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all">\r\n                            <option value="short">Courtes</option>\r\n                            <option value="medium" selected>Moyennes</option>\r\n                            <option value="long">Détaillées</option>\r\n                        </select>\r\n                    </div>\r\n\r\n                    <!-- Data Training Consent -->\r\n                    <div class="space-y-2">\r\n                        <label class="block text-sm font-semibold text-gray-700 dark:text-gray-300 flex items-center">\r\n                            <i class="fas fa-database mr-2 text-red-500"></i>\r\n                            Utilisation des données\r\n                        </label>\r\n                        <div\r\n                            class="p-4 bg-gray-50 dark:bg-slate-700 rounded-lg border border-gray-200 dark:border-slate-600">\r\n                            <div class="flex items-start justify-between">\r\n                                <div class="flex-1 pr-3">\r\n                                    <p class="text-sm text-gray-700 dark:text-gray-300 mb-1">\r\n                                        Améliorer l'IA avec mes conversations\r\n                                    </p>\r\n                                    <p class="text-xs text-gray-500 dark:text-gray-400">\r\n                                        Vos données seront anonymisées et utilisées pour entraîner le modèle\r\n                                    </p>\r\n                                </div>\r\n                                <label class="toggle-switch">\r\n                                    <input type="checkbox" id="dataTrainingToggle">\r\n                                    <span class="toggle-slider"></span>\r\n                                </label>\r\n                            </div>\r\n                        </div>\r\n                    </div>\r\n\r\n                    <!-- Auto-save Conversations -->\r\n                    <div class="space-y-2">\r\n                        <label class="block text-sm font-semibold text-gray-700 dark:text-gray-300 flex items-center">\r\n                            <i class="fas fa-save mr-2 text-teal-500"></i>\r\n                            Sauvegarde automatique\r\n                        </label>\r\n                        <div\r\n                            class="flex items-center justify-between p-4 bg-gray-50 dark:bg-slate-700 rounded-lg border border-gray-200 dark:border-slate-600">\r\n                            <span class="text-sm font-medium text-gray-700 dark:text-gray-300">Sauvegarder les\r\n                                conversations</span>\r\n                            <label class="toggle-switch">\r\n                                <input type="checkbox" id="autoSaveToggle" checked>\r\n                                <span class="toggle-slider"></span>\r\n                            </label>\r\n                        </div>\r\n                    </div>\r\n\r\n                    <!-- Notifications -->\r\n                    <div class="space-y-2">\r\n                        <label class="block text-sm font-semibold text-gray-700 dark:text-gray-300 flex items-center">\r\n                            <i class="fas fa-bell mr-2 text-yellow-500"></i>\r\n                            Notifications\r\n                        </label>\r\n                        <div\r\n                            class="flex items-center justify-between p-4 bg-gray-50 dark:bg-slate-700 rounded-lg border border-gray-200 dark:border-slate-600">\r\n                            <span class="text-sm font-medium text-gray-700 dark:text-gray-300">Activer les\r\n                                notifications</span>\r\n                            <label class="toggle-switch">\r\n                                <input type="checkbox" id="notificationsToggle" checked>\r\n                                <span class="toggle-slider"></span>\r\n                            </label>\r\n                        </div>\r\n                    </div>\r\n                </div>\r\n            </div>\r\n\r\n            <!-- Modal Footer -->\r\n            <div\r\n                class="px-6 py-4 bg-gray-50 dark:bg-slate-700 border-t border-gray-200 dark:border-slate-600 flex justify-between items-center">\r\n                <button id="resetSettingsBtn"\r\n                    class="px-4 py-2 text-sm font-medium text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-gray-200 transition-colors">\r\n                    <i class="fas fa-undo mr-2"></i>\r\n                    Réinitialiser\r\n                </button>\r\n                <button id="saveSettingsBtn"\r\n                    class="px-6 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg text-sm font-medium transition-all shadow-md hover:shadow-lg">\r\n                    <i class="fas fa-check mr-2"></i>\r\n                    Enregistrer\r\n                </button>\r\n            </div>\r\n        </div>\r\n    </div>\r\n\r\n    <script>\r\n        class DefAIChat {\r\n            constructor() {\r\n                this.currentConversationId = null;\r\n                this.messages = [];\r\n                this.isTyping = false;\r\n                this.typingSpeed = 20;\r\n                this.attachments = [];\r\n                this.quota = 2;\r\n                this.conversationStarted = false;\r\n                this.settings = this.loadSettings();\r\n                this.init();\r\n            }\r\n\r\n            init() {\r\n                this.bindEvents();\r\n                this.loadConversations();\r\n                this.checkActiveConversation();\r\n                this.autoResizeTextarea();\r\n                this.updateQuotaDisplay();\r\n                this.applyTheme();\r\n                this.loadSettingsToUI();\r\n            }\r\n\r\n            loadSettings() {\r\n                const defaultSettings = {\r\n                    tone: 'professional',\r\n                    theme: 'light',\r\n                    language: 'fr',\r\n                    responseLength: 'medium',\r\n                    dataTraining: false,\r\n                    autoSave: true,\r\n                    notifications: true\r\n                };\r\n\r\n                const saved = localStorage.getItem('defai_settings');\r\n                return saved ? { ...defaultSettings, ...JSON.parse(saved) } : defaultSettings;\r\n            }\r\n\r\n            saveSettings() {\r\n                localStorage.setItem('defai_settings', JSON.stringify(this.settings));\r\n            }\r\n\r\n            loadSettingsToUI() {\r\n                document.getElementById('toneSetting').value = this.settings.tone;\r\n                document.getElementById('themeToggle').checked = this.settings.theme === 'dark';\r\n                document.getElementById('languageSetting').value = this.settings.language;\r\n                document.getElementById('responseLengthSetting').value = this.settings.responseLength;\r\n                document.getElementById('dataTrainingToggle').checked = this.settings.dataTraining;\r\n                document.getElementById('autoSaveToggle').checked = this.settings.autoSave;\r\n                document.getElementById('notificationsToggle').checked = this.settings.notifications;\r\n            }\r\n\r\n            applyTheme() {\r\n                if (this.settings.theme === 'dark') {\r\n                    document.documentElement.setAttribute('data-theme', 'dark');\r\n                    document.body.classList.add('dark');\r\n                } else {\r\n                    document.documentElement.setAttribute('data-theme', 'light');\r\n                    document.body.classList.remove('dark');\r\n                }\r\n            }\r\n\r\n            bindEvents() {\r\n                const messageInput = document.getElementById('messageInput');\r\n                const sendBtn = document.getElementById('sendBtn');\r\n                const sendIcon = document.getElementById('sendIcon');\r\n\r\n                sendBtn.addEventListener('click', () => {\r\n                    sendIcon.classList.toggle('fa-stop');\r\n                    this.sendMessage();\r\n                });\r\n\r\n                messageInput.addEventListener('keydown', (e) => {\r\n                    if (e.key === 'Enter' && !e.shiftKey) {\r\n                        e.preventDefault();\r\n                        this.sendMessage();\r\n                    }\r\n                });\r\n\r\n                messageInput.addEventListener('input', () => this.autoResizeTextarea());\r\n\r\n                document.getElementById('attachBtn').addEventListener('click', () => this.handleFileAttach());\r\n                document.getElementById('generateImageBtn').addEventListener('click', () => this.requestImageGeneration());\r\n                document.getElementById('clearAttachments').addEventListener('click', () => this.clearAttachments());\r\n                document.getElementById('fileInput').addEventListener('change', (e) => this.handleFileSelection(e));\r\n\r\n                document.getElementById('newConversationBtn').addEventListener('click', () => this.createNewConversation());\r\n                document.getElementById('clearChatBtn').addEventListener('click', () => this.clearCurrentChat());\r\n                document.getElementById('toggleConversationsBtn').addEventListener('click', () => this.toggleConversationsSidebar());\r\n                document.getElementById('openSidebarBtn').addEventListener('click', () => this.toggleSidebarPanel(true));\r\n                document.getElementById('closeSidebarBtn').addEventListener('click', () => this.toggleSidebarPanel(false));\r\n                document.getElementById('mobileOverlay').addEventListener('click', () => this.closeAllSidebars());\r\n\r\n                // Settings Modal Events\r\n                document.getElementById('openSettingsBtn').addEventListener('click', () => this.openSettingsModal());\r\n                document.getElementById('closeSettingsBtn').addEventListener('click', () => this.closeSettingsModal());\r\n                document.getElementById('saveSettingsBtn').addEventListener('click', () => this.saveSettingsFromModal());\r\n                document.getElementById('resetSettingsBtn').addEventListener('click', () => this.resetSettings());\r\n\r\n                // Theme Toggle in Modal\r\n                document.getElementById('themeToggle').addEventListener('change', (e) => {\r\n                    this.settings.theme = e.target.checked ? 'dark' : 'light';\r\n                    this.applyTheme();\r\n                });\r\n\r\n                document.getElementById('downloadPdfBtn').addEventListener('click', () => this.downloadPDF());\r\n                document.getElementById('downloadTxtBtn').addEventListener('click', () => this.downloadTXT());\r\n                document.getElementById('copyAllBtn').addEventListener('click', () => this.copyAllMessages());\r\n            }\r\n\r\n            openSettingsModal() {\r\n                document.getElementById('settingsModal').classList.remove('hidden');\r\n                this.loadSettingsToUI();\r\n            }\r\n\r\n            closeSettingsModal() {\r\n                document.getElementById('settingsModal').classList.add('hidden');\r\n            }\r\n\r\n            async saveSettingsFromModal() {\r\n                // Récupérer les valeurs\r\n                this.settings.tone = document.getElementById('toneSetting').value;\r\n                this.settings.theme = document.getElementById('themeToggle').checked ? 'dark' : 'light';\r\n                this.settings.language = document.getElementById('languageSetting').value;\r\n                this.settings.responseLength = document.getElementById('responseLengthSetting').value;\r\n                this.settings.dataTraining = document.getElementById('dataTrainingToggle').checked;\r\n                this.settings.autoSave = document.getElementById('autoSaveToggle').checked;\r\n                this.settings.notifications = document.getElementById('notificationsToggle').checked;\r\n\r\n                // Sauvegarder localement\r\n                this.saveSettings();\r\n\r\n                // Envoyer au backend (simulation pour le moment)\r\n                try {\r\n                    // TODO: Remplacer par votre endpoint réel\r\n                    const response = await fetch('/api/settings/save', {\r\n                        method: 'POST',\r\n                        headers: {\r\n                            'Content-Type': 'application/json',\r\n                            'X-CSRFToken': this.getCSRFToken()\r\n                        },\r\n                        body: JSON.stringify(this.settings)\r\n                    });\r\n\r\n                    // Pour le moment, afficher une alerte\r\n                    alert(`Paramètres sauvegardés !\\n\\nTon: ${this.settings.tone}\\nThème: ${this.settings.theme}\\nLangue: ${this.settings.language}\\nLongueur: ${this.settings.responseLength}\\nEntraînement IA: ${this.settings.dataTraining ? 'Oui' : 'Non'}\\nSauvegarde auto: ${this.settings.autoSave ? 'Oui' : 'Non'}\\nNotifications: ${this.settings.notifications ? 'Oui' : 'Non'}`);\r\n\r\n                    this.showNotification('Paramètres enregistrés', 'success');\r\n                    this.closeSettingsModal();\r\n                } catch (error) {\r\n                    console.error('Error saving settings:', error);\r\n                    alert('Paramètres enregistrés localement (endpoint backend à configurer)');\r\n                    this.showNotification('Paramètres enregistrés localement', 'success');\r\n                    this.closeSettingsModal();\r\n                }\r\n            }\r\n\r\n            resetSettings() {\r\n                if (confirm('Réinitialiser tous les paramètres par défaut ?')) {\r\n                    this.settings = {\r\n                        tone: 'professional',\r\n                        theme: 'light',\r\n                        language: 'fr',\r\n                        responseLength: 'medium',\r\n                        dataTraining: false,\r\n                        autoSave: true,\r\n                        notifications: true\r\n                    };\r\n                    this.saveSettings();\r\n                    this.applyTheme();\r\n                    this.loadSettingsToUI();\r\n                    this.showNotification('Paramètres réinitialisés', 'success');\r\n                }\r\n            }\r\n\r\n            showConversationUI() {\r\n                if (!this.conversationStarted) {\r\n                    this.conversationStarted = true;\r\n                    document.getElementById('welcomeScreen').classList.add('hidden');\r\n                }\r\n            }\r\n\r\n            hideConversationUI() {\r\n                this.conversationStarted = false;\r\n                document.getElementById('welcomeScreen').classList.remove('hidden');\r\n            }\r\n\r\n            autoResizeTextarea() {\r\n                const textarea = document.getElementById('messageInput');\r\n                textarea.style.height = 'auto';\r\n                textarea.style.height = Math.min(textarea.scrollHeight, 120) + 'px';\r\n            }\r\n\r\n            toggleConversationsSidebar() {\r\n                const sidebar = document.getElementById('conversationsSidebar');\r\n                const overlay = document.getElementById('mobileOverlay');\r\n\r\n                sidebar.classList.toggle('sidebar-hidden');\r\n                sidebar.classList.toggle('sidebar-visible');\r\n\r\n                if (!sidebar.classList.contains('sidebar-hidden')) {\r\n                    overlay.classList.remove('hidden');\r\n                } else {\r\n                    overlay.classList.add('hidden');\r\n                }\r\n            }\r\n\r\n            toggleSidebarPanel(show) {\r\n                const panel = document.getElementById('sidebarPanel');\r\n                const overlay = document.getElementById('mobileOverlay');\r\n\r\n                if (show) {\r\n                    panel.classList.remove('translate-x-full');\r\n                    overlay.classList.remove('hidden');\r\n                } else {\r\n                    panel.classList.add('translate-x-full');\r\n                    overlay.classList.add('hidden');\r\n                }\r\n            }\r\n\r\n            closeAllSidebars() {\r\n                document.getElementById('conversationsSidebar').classList.add('sidebar-hidden');\r\n                document.getElementById('conversationsSidebar').classList.remove('sidebar-visible');\r\n                document.getElementById('sidebarPanel').classList.add('translate-x-full');\r\n                document.getElementById('mobileOverlay').classList.add('hidden');\r\n                this.closeSettingsModal();\r\n            }\r\n\r\n            scrollToBottom(smooth = true) {\r\n                const container = document.getElementById('messagesContainer');\r\n                if (!container) return;\r\n                const behavior = smooth ? 'smooth' : 'auto';\r\n                requestAnimationFrame(() => {\r\n                    container.scrollTo({ top: container.scrollHeight, behavior });\r\n                });\r\n            }\r\n\r\n            getCSRFToken() {\r\n                const token = document.querySelector('meta[name="csrf-token"]');\r\n                return token ? token.getAttribute('content') : '';\r\n            }\r\n\r\n            async loadConversations() {\r\n                const container = document.getElementById('conversationsList');\r\n\r\n                try {\r\n                    const response = await fetch('/ai/conversations', {\r\n                        method: 'GET',\r\n                        headers: {\r\n                            'Content-Type': 'application/json',\r\n                            'X-CSRFToken': this.getCSRFToken()\r\n                        }\r\n                    });\r\n\r\n                    if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);\r\n\r\n                    const conversations = await response.json();\r\n                    container.innerHTML = '';\r\n\r\n                    if (conversations.length === 0) {\r\n                        container.innerHTML = '<div class="p-4 text-center text-gray-500 dark:text-gray-400 text-xs">Aucune conversation</div>';\r\n                        return;\r\n                    }\r\n\r\n                    conversations.forEach(conv => {\r\n                        const item = document.createElement('div');\r\n                        const isActive = conv.id === this.currentConversationId;\r\n                        item.className = `cursor-pointer rounded-lg m-2 p-3 transition-all hover:bg-blue-50 dark:hover:bg-slate-700 ${isActive ? 'bg-blue-50 dark:bg-slate-700' : 'bg-white dark:bg-slate-800'}`;\r\n                        item.innerHTML = `\r\n                            <div class="flex justify-between items-start">\r\n                                <div class="flex-1 min-w-0 pr-2">\r\n                                    <h3 class="font-semibold text-gray-900 dark:text-gray-100 truncate text-xs mb-1">${this.escapeHtml(conv.title)}</h3>\r\n                                    <p class="text-xs text-gray-500 dark:text-gray-400 truncate">${this.escapeHtml(conv.last_message || 'Pas de message')}</p>\r\n                                </div>\r\n                                <button class="text-gray-400 dark:text-gray-500 hover:text-red-500 dark:hover:text-red-400 p-1 rounded transition-colors" onclick="event.stopPropagation(); chat.deleteConversation(${conv.id})">\r\n                                    <i class="fas fa-trash text-xs"></i>\r\n                                </button>\r\n                            </div>\r\n                        `;\r\n                        item.addEventListener('click', () => {\r\n                            this.loadConversation(conv.id);\r\n                            this.closeAllSidebars();\r\n                        });\r\n                        container.appendChild(item);\r\n                    });\r\n                } catch (error) {\r\n                    console.error('Error loading conversations:', error);\r\n                    container.innerHTML = '<div class="p-4 text-center text-red-500 dark:text-red-400 text-xs">Erreur de chargement</div>';\r\n                }\r\n            }\r\n\r\n            async createNewConversation() {\r\n                try {\r\n                    const response = await fetch('/ai/conversations', {\r\n                        method: 'POST',\r\n                        headers: {\r\n                            'Content-Type': 'application/json',\r\n                            'X-CSRFToken': this.getCSRFToken()\r\n                        },\r\n                        body: JSON.stringify({ title: 'Nouvelle conversation' })\r\n                    });\r\n\r\n                    if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);\r\n\r\n                    const conversation = await response.json();\r\n                    this.currentConversationId = conversation.id;\r\n                    this.messages = [];\r\n                    this.hideConversationUI();\r\n                    this.loadConversations();\r\n                    this.showNotification('Nouvelle conversation créée', 'success');\r\n                } catch (error) {\r\n                    console.error('Error creating conversation:', error);\r\n                    this.showNotification('Erreur lors de la création', 'error');\r\n                }\r\n            }\r\n\r\n            async loadConversation(conversationId) {\r\n                try {\r\n                    const response = await fetch(`/ai/conversations/${conversationId}`, {\r\n                        method: 'GET',\r\n                        headers: {\r\n                            'Content-Type': 'application/json',\r\n                            'X-CSRFToken': this.getCSRFToken()\r\n                        }\r\n                    });\r\n\r\n                    if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);\r\n\r\n                    const data = await response.json();\r\n                    this.currentConversationId = conversationId;\r\n                    this.messages = data.messages || [];\r\n\r\n                    if (this.messages.length > 0) {\r\n                        this.showConversationUI();\r\n                    }\r\n\r\n                    this.renderMessages();\r\n                    this.loadConversations();\r\n                } catch (error) {\r\n                    console.error('Error loading conversation:', error);\r\n                    this.showNotification('Erreur lors du chargement', 'error');\r\n                }\r\n            }\r\n\r\n            renderMessages() {\r\n                const container = document.getElementById('messagesContainer');\r\n\r\n                if (this.messages.length === 0) {\r\n                    if (!this.conversationStarted) {\r\n                        container.innerHTML = `\r\n                            <div id="welcomeScreen" class="h-full flex flex-col items-center justify-center">\r\n                                <div class="text-center max-w-2xl mx-auto px-4 animate-fade-in">\r\n                                    <div class="inline-block p-6 rounded-full mb-6 bg-gradient-to-br from-blue-50 to-blue-100 dark:from-blue-900/30 dark:to-blue-800/30">\r\n                                        <i class="fas fa-robot text-6xl text-blue-600 dark:text-blue-400"></i>\r\n                                    </div>\r\n                                    <h2 class="text-3xl sm:text-4xl font-bold mb-3 text-gray-900 dark:text-gray-100">Bienvenue sur defAI</h2>\r\n                                    <p class="text-base sm:text-lg text-gray-600 dark:text-gray-400">Votre assistant intelligent pour tous vos besoins</p>\r\n                                </div>\r\n                            </div>\r\n                        `;\r\n                    }\r\n                    this.updateStats();\r\n                    return;\r\n                }\r\n\r\n                container.innerHTML = '<div class="max-w-3xl mx-auto space-y-4"></div>';\r\n                const messagesWrapper = container.firstChild;\r\n\r\n                this.messages.forEach((message, index) => {\r\n                    const messageElement = this.createMessageElement(message, index);\r\n                    messagesWrapper.appendChild(messageElement);\r\n                });\r\n\r\n                this.scrollToBottom(false);\r\n                this.updateStats();\r\n            }\r\n\r\n            createMessageElement(message, index) {\r\n                const div = document.createElement('div');\r\n                const isUser = message.message_type === 'user';\r\n\r\n                div.className = `flex ${isUser ? 'justify-end' : 'justify-start'} animate-slide-in`;\r\n\r\n                const bubbleClasses = isUser\r\n                    ? 'bg-blue-600 text-white rounded-2xl rounded-br-sm'\r\n                    : 'bg-gray-100 dark:bg-slate-700 text-gray-900 dark:text-gray-100 rounded-2xl rounded-bl-sm';\r\n\r\n                let attachmentsHtml = '';\r\n                if (message.attachments && message.attachments.length > 0) {\r\n                    attachmentsHtml = message.attachments.map(attachment => {\r\n                        if (attachment.type === 'generated_image') {\r\n                            if (attachment.status === 'generating') {\r\n                                return `\r\n                        <div class="image-placeholder mt-2" data-task-id="${attachment.task_id}">\r\n                            <div class="flex items-center justify-center p-3 bg-gray-50 dark:bg-slate-600 rounded-lg border-2 border-dashed border-gray-300 dark:border-slate-500">\r\n                                <div class="text-center">\r\n                                    <div class="animate-spin rounded-full h-6 w-6 border-b-2 border-blue-500 mx-auto mb-2"></div>\r\n                                    <p class="text-xs text-gray-600 dark:text-gray-300">Génération en cours...</p>\r\n                                    <p class="text-xs text-gray-500 dark:text-gray-400 mt-1">${this.escapeHtml(attachment.prompt)}</p>\r\n                                </div>\r\n                            </div>\r\n                        </div>\r\n                    `;\r\n                            } else if (attachment.status === 'completed') {\r\n                                const imageUrl = attachment.image_url || buildImageUrl(this.currentConversationId, attachment.name);\r\n                                return `\r\n                        <div class="generated-image-container mt-2">\r\n                            <img \r\n                                src="${imageUrl}" \r\n                                alt="${this.escapeHtml(attachment.prompt)}" \r\n                                class="max-w-full h-auto rounded-lg shadow-md cursor-pointer hover:shadow-xl transition-shadow" \r\n                                onclick="chat.openImageModal('${imageUrl}')"\r\n                                onerror="this.onerror=null; this.src='/static/assets/image-error.png'; this.classList.add('opacity-50');"\r\n                            />\r\n                            <div class="mt-1 text-xs text-gray-500 dark:text-gray-400">\r\n                                <p>📷 ${this.escapeHtml(attachment.prompt)}</p>\r\n                            </div>\r\n                        </div>\r\n                    `;\r\n                            } else if (attachment.status === 'error') {\r\n                                return `\r\n                        <div class="image-error mt-2">\r\n                            <div class="flex items-center justify-center p-3 bg-red-50 dark:bg-red-900/30 rounded-lg border-2 border-red-200 dark:border-red-800">\r\n                                <div class="text-center">\r\n                                    <i class="fas fa-exclamation-triangle text-red-500 text-lg mb-1"></i>\r\n                                    <p class="text-xs text-red-600 dark:text-red-400">Erreur génération</p>\r\n                                    <p class="text-xs text-red-500 dark:text-red-400 mt-1">${this.escapeHtml(attachment.error || 'Erreur inconnue')}</p>\r\n                                </div>\r\n                            </div>\r\n                        </div>\r\n                    `;\r\n                            } else if (attachment.status === 'queued') {\r\n                                return `\r\n                        <div class="image-placeholder mt-2" data-task-id="${attachment.task_id}">\r\n                            <div class="flex items-center justify-center p-3 bg-yellow-50 dark:bg-yellow-900/30 rounded-lg border-2 border-dashed border-yellow-300 dark:border-yellow-700">\r\n                                <div class="text-center">\r\n                                    <i class="fas fa-clock text-yellow-500 text-lg mb-1"></i>\r\n                                    <p class="text-xs text-yellow-700 dark:text-yellow-400">En attente...</p>\r\n                                    <p class="text-xs text-yellow-600 dark:text-yellow-500 mt-1">${this.escapeHtml(attachment.prompt)}</p>\r\n                                </div>\r\n                            </div>\r\n                        </div>\r\n                    `;\r\n                            }\r\n                        }\r\n                        return '';\r\n                    }).join('');\r\n                }\r\n\r\n                div.innerHTML = `\r\n        <div class="max-w-[80%] ${bubbleClasses} px-4 py-2 shadow-sm">\r\n            <div class="text-sm leading-relaxed markdown-content" id="message-${index}"></div>\r\n            ${attachmentsHtml}\r\n            <div class="text-xs ${isUser ? 'text-white/70' : 'text-gray-500 dark:text-gray-400'} mt-1 flex items-center justify-between">\r\n                <span>${this.formatTime(message.created_at)}</span>\r\n                ${!isUser ? `<button onclick="chat.copyMessage(${index})" class="ml-2 hover:text-gray-700 dark:hover:text-gray-300 transition-colors"><i class="fas fa-copy"></i></button>` : ''}\r\n            </div>\r\n        </div>\r\n    `;\r\n\r\n                setTimeout(() => {\r\n                    const contentDiv = document.getElementById(`message-${index}`);\r\n                    if (contentDiv) {\r\n                        if (isUser) {\r\n                            contentDiv.textContent = message.content;\r\n                        } else {\r\n                            contentDiv.innerHTML = marked.parse(message.content);\r\n                        }\r\n                    }\r\n                }, 0);\r\n\r\n                return div;\r\n            }\r\n\r\n            openImageModal(imageUrl) {\r\n                const modal = document.createElement('div');\r\n                modal.className = 'fixed inset-0 bg-black/80 backdrop-blur-sm z-50 flex items-center justify-center p-4';\r\n                modal.onclick = () => modal.remove();\r\n\r\n                modal.innerHTML = `\r\n        <div class="relative max-w-4xl max-h-[90vh]">\r\n            <button \r\n                onclick="event.stopPropagation(); this.closest('.fixed').remove();" \r\n                class="absolute -top-10 right-0 text-white hover:text-gray-300 text-2xl"\r\n            >\r\n                <i class="fas fa-times"></i>\r\n            </button>\r\n            <img \r\n                src="${imageUrl}" \r\n                class="max-w-full max-h-[85vh] rounded-lg shadow-2xl"\r\n                onclick="event.stopPropagation();"\r\n            />\r\n        </div>\r\n    `;\r\n\r\n                document.body.appendChild(modal);\r\n            }\r\n\r\n            async sendMessage() {\r\n                const input = document.getElementById('messageInput');\r\n                const message = input.value.trim();\r\n\r\n                if (!message || this.isTyping) return;\r\n\r\n                this.showConversationUI();\r\n                this.addMessage('user', message);\r\n                input.value = '';\r\n                this.autoResizeTextarea();\r\n\r\n                this.showTypingIndicator();\r\n\r\n                let retryCount = 0;\r\n                const maxRetries = 3;\r\n\r\n                const attemptSend = async () => {\r\n                    try {\r\n                        const formData = new FormData();\r\n                        formData.append('message', message);\r\n                        formData.append('conversation_id', this.currentConversationId);\r\n                        formData.append('settings', JSON.stringify(this.settings));\r\n\r\n                        const response = await fetch('/ai/chat', {\r\n                            method: 'POST',\r\n                            headers: {\r\n                                'X-CSRFToken': this.getCSRFToken()\r\n                            },\r\n                            body: formData\r\n                        });\r\n\r\n                        if (!response.ok) {\r\n                            throw new Error(`HTTP error! status: ${response.status}`);\r\n                        }\r\n\r\n                        const data = await response.json();\r\n\r\n                        if (data.success) {\r\n                            if (data.conversation_id && !this.currentConversationId) {\r\n                                this.currentConversationId = data.conversation_id;\r\n                            }\r\n\r\n                            if (data.internal_requests && data.internal_requests.length > 0) {\r\n                                this.showInternalRequests(data.internal_requests);\r\n                                await new Promise(resolve => setTimeout(resolve, 1000));\r\n                            }\r\n\r\n                            const attachmentsWithImages = (data.attachments || []).map(att => {\r\n                                if (att.status === 'generating' && att.task_id) {\r\n                                    return {\r\n                                        ...att,\r\n                                        type: 'generated_image',\r\n                                        status: 'generating'\r\n                                    };\r\n                                }\r\n                                return att;\r\n                            });\r\n\r\n                            await this.typeMessage(data.response, attachmentsWithImages);\r\n\r\n                            if (attachmentsWithImages.some(att => att.status === 'generating')) {\r\n                                this.pollImageGeneration(this.messages.length - 1, attachmentsWithImages);\r\n                            }\r\n\r\n                            if (data.conversation_updated) {\r\n                                this.loadConversations();\r\n                            }\r\n                        } else {\r\n                            const fallbackMessage = data.error_type === 'gemini_unavailable'\r\n                                ? "Le service est temporairement indisponible."\r\n                                : `Erreur: ${data.error || 'Erreur serveur'}`;\r\n                            this.addMessage('assistant', fallbackMessage);\r\n                        }\r\n                    } catch (error) {\r\n                        console.error('Error sending message:', error);\r\n\r\n                        if (retryCount < maxRetries) {\r\n                            retryCount++;\r\n                            this.showNotification(`Tentative ${retryCount}/${maxRetries}...`, 'error');\r\n                            await new Promise(resolve => setTimeout(resolve, 2000));\r\n                            return attemptSend();\r\n                        } else {\r\n                            this.addMessage('assistant', 'Désolé, une erreur est survenue après plusieurs tentatives.');\r\n                        }\r\n                    } finally {\r\n                        this.hideTypingIndicator();\r\n                        this.hideInternalRequestsIndicator();\r\n                        this.updateStats();\r\n                    }\r\n                };\r\n\r\n                await attemptSend();\r\n            }\r\n\r\n            async pollImageGeneration(messageIndex, attachments) {\r\n                const generatingAttachment = attachments.find(att => att.status === 'generating');\r\n                if (!generatingAttachment) return;\r\n\r\n                const taskId = generatingAttachment.task_id;\r\n                if (!taskId) {\r\n                    this.updateImageStatus(messageIndex, 'error', 'ID de tâche manquant');\r\n                    return;\r\n                }\r\n\r\n                let attempts = 0;\r\n                const maxAttempts = 60;\r\n                let delay = 5000;\r\n                const maxDelay = 30000;\r\n\r\n                const poll = async () => {\r\n                    if (attempts >= maxAttempts) {\r\n                        this.updateImageStatus(messageIndex, 'error', 'Délai dépassé');\r\n                        return;\r\n                    }\r\n\r\n                    try {\r\n                        const response = await fetch(\r\n                            `/ai/image-status/${this.currentConversationId}?task_id=${encodeURIComponent(taskId)}`,\r\n                            {\r\n                                method: 'GET',\r\n                                headers: {\r\n                                    'X-CSRFToken': this.getCSRFToken()\r\n                                }\r\n                            }\r\n                        );\r\n\r\n                        if (response.ok) {\r\n                            const data = await response.json();\r\n\r\n                            if (data.status === 'completed') {\r\n                                this.updateImageInMessage(messageIndex, data.image_url, data.filename);\r\n                                this.showNotification('Image générée avec succès !', 'success');\r\n                                return;\r\n                            } else if (data.status === 'error') {\r\n                                this.updateImageStatus(messageIndex, 'error', data.error);\r\n                                this.showNotification('Erreur génération image', 'error');\r\n                                return;\r\n                            } else if (data.status === 'queued') {\r\n                                this.updateQueuePosition(messageIndex, data.queue_position);\r\n                            }\r\n                        }\r\n\r\n                        attempts++;\r\n                        delay = Math.min(delay * 1.2, maxDelay);\r\n                        setTimeout(poll, delay);\r\n                    } catch (error) {\r\n                        console.error('Error polling image:', error);\r\n                        attempts++;\r\n                        setTimeout(poll, delay * 2);\r\n                    }\r\n                };\r\n\r\n                poll();\r\n            }\r\n\r\n            updateQueuePosition(messageIndex, position) {\r\n                const message = this.messages[messageIndex];\r\n                if (message && message.attachments) {\r\n                    const container = document.getElementById('messagesContainer');\r\n                    const placeholders = container.querySelectorAll('.image-placeholder');\r\n\r\n                    placeholders.forEach(placeholder => {\r\n                        const queueInfo = placeholder.querySelector('.queue-info');\r\n                        if (queueInfo) {\r\n                            queueInfo.textContent = `Position dans la file: ${position}`;\r\n                        } else {\r\n                            const info = document.createElement('p');\r\n                            info.className = 'text-xs text-gray-500 dark:text-gray-400 mt-1 queue-info';\r\n                            info.textContent = `Position dans la file: ${position}`;\r\n                            placeholder.querySelector('.text-center').appendChild(info);\r\n                        }\r\n                    });\r\n                }\r\n            }\r\n\r\n            updateImageInMessage(messageIndex, imageUrl, filename) {\r\n                const message = this.messages[messageIndex];\r\n                if (message && message.attachments) {\r\n                    message.attachments = message.attachments.map(att => {\r\n                        if (att.status === 'generating') {\r\n                            return {\r\n                                ...att,\r\n                                status: 'completed',\r\n                                name: filename,\r\n                                image_url: imageUrl\r\n                            };\r\n                        }\r\n                        return att;\r\n                    });\r\n                    this.renderMessages();\r\n                }\r\n            }\r\n\r\n            updateImageStatus(messageIndex, status, error) {\r\n                const message = this.messages[messageIndex];\r\n                if (message && message.attachments) {\r\n                    message.attachments = message.attachments.map(att => {\r\n                        if (att.status === 'generating') {\r\n                            return {\r\n                                ...att,\r\n                                status: status,\r\n                                error: error\r\n                            };\r\n                        }\r\n                        return att;\r\n                    });\r\n                    this.renderMessages();\r\n                }\r\n            }\r\n\r\n            showInternalRequests(internalRequests) {\r\n                this.hideTypingIndicator();\r\n\r\n                const container = document.getElementById('messagesContainer').firstChild;\r\n                const indicator = document.createElement('div');\r\n                indicator.id = 'internalRequestsIndicator';\r\n                indicator.className = 'flex justify-start animate-slide-in';\r\n\r\n                const requestsHtml = internalRequests.map(req => {\r\n                    const statusIcon = req.status === 'success'\r\n                        ? '<i class="fas fa-check-circle text-green-500"></i>'\r\n                        : '<i class="fas fa-exclamation-circle text-red-500"></i>';\r\n\r\n                    return `\r\n                        <div class="flex items-start mb-1 p-2 bg-gray-50 dark:bg-slate-600 rounded">\r\n                            <div class="mr-2 mt-0.5">${statusIcon}</div>\r\n                            <div class="flex-1">\r\n                                <div class="text-xs font-medium text-gray-700 dark:text-gray-300">${this.escapeHtml(req.description)}</div>\r\n                            </div>\r\n                        </div>\r\n                    `;\r\n                }).join('');\r\n\r\n                indicator.innerHTML = `\r\n                    <div class="bg-blue-50 dark:bg-blue-900/30 border border-blue-200 dark:border-blue-800 rounded-2xl rounded-bl-sm shadow-sm px-3 py-2">\r\n                        <div class="text-xs font-medium text-blue-700 dark:text-blue-400 mb-2 flex items-center">\r\n                            <i class="fas fa-database mr-1"></i>\r\n                            Requêtes (${internalRequests.length})\r\n                        </div>\r\n                        ${requestsHtml}\r\n                    </div>\r\n                `;\r\n\r\n                container.appendChild(indicator);\r\n                this.scrollToBottom();\r\n            }\r\n\r\n            hideInternalRequestsIndicator() {\r\n                const indicator = document.getElementById('internalRequestsIndicator');\r\n                if (indicator) {\r\n                    indicator.style.transition = 'opacity 0.5s';\r\n                    indicator.style.opacity = '0';\r\n                    setTimeout(() => indicator.remove(), 500);\r\n                }\r\n            }\r\n\r\n            async typeMessage(text, attachments = []) {\r\n                return new Promise((resolve) => {\r\n                    this.hideTypingIndicator();\r\n\r\n                    const messageObj = {\r\n                        message_type: 'assistant',\r\n                        content: '',\r\n                        created_at: new Date().toISOString(),\r\n                        attachments: attachments\r\n                    };\r\n\r\n                    this.messages.push(messageObj);\r\n                    const messageIndex = this.messages.length - 1;\r\n\r\n                    const container = document.getElementById('messagesContainer').firstChild;\r\n                    const messageElement = this.createMessageElement(messageObj, messageIndex);\r\n                    container.appendChild(messageElement);\r\n\r\n                    const messageContentDiv = document.getElementById(`message-${messageIndex}`);\r\n\r\n                    let currentIndex = 0;\r\n\r\n                    const typeNextChar = () => {\r\n                        if (currentIndex < text.length) {\r\n                            messageObj.content += text[currentIndex];\r\n                            messageContentDiv.innerHTML = marked.parse(messageObj.content) + '<span class="cursor-blink"></span>';\r\n                            currentIndex++;\r\n\r\n                            this.scrollToBottom();\r\n                            setTimeout(typeNextChar, this.typingSpeed);\r\n                        } else {\r\n                            messageContentDiv.innerHTML = marked.parse(messageObj.content);\r\n                            this.updateStats();\r\n                            resolve();\r\n                        }\r\n                    };\r\n\r\n                    typeNextChar();\r\n                });\r\n            }\r\n\r\n            addMessage(type, content) {\r\n                const message = {\r\n                    message_type: type,\r\n                    content: content,\r\n                    created_at: new Date().toISOString()\r\n                };\r\n\r\n                this.messages.push(message);\r\n                this.renderMessages();\r\n            }\r\n\r\n            showTypingIndicator() {\r\n                this.isTyping = true;\r\n                const container = document.getElementById('messagesContainer').firstChild;\r\n                const indicator = document.createElement('div');\r\n                indicator.id = 'typingIndicator';\r\n                indicator.className = 'flex justify-start animate-slide-in';\r\n                indicator.innerHTML = `\r\n                    <div class="bg-gray-100 dark:bg-slate-700 rounded-2xl rounded-bl-sm shadow-sm px-4 py-3">\r\n                        <div class="flex items-center gap-1">\r\n                            <span class="typing-dot rounded-full bg-blue-600"></span>\r\n                            <span class="typing-dot rounded-full bg-blue-600"></span>\r\n                            <span class="typing-dot rounded-full bg-blue-600"></span>\r\n                        </div>\r\n                    </div>\r\n                `;\r\n                container.appendChild(indicator);\r\n                this.scrollToBottom();\r\n            }\r\n\r\n            hideTypingIndicator() {\r\n                this.isTyping = false;\r\n                const indicator = document.getElementById('typingIndicator');\r\n                if (indicator) indicator.remove();\r\n            }\r\n\r\n            async deleteConversation(id) {\r\n                if (!confirm('Supprimer cette conversation ?')) return;\r\n\r\n                try {\r\n                    const response = await fetch(`/ai/conversations/${id}`, {\r\n                        method: 'DELETE',\r\n                        headers: {\r\n                            'Content-Type': 'application/json',\r\n                            'X-CSRFToken': this.getCSRFToken()\r\n                        }\r\n                    });\r\n\r\n                    if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);\r\n\r\n                    if (id === this.currentConversationId) 